// src/pages/TeamSheet.js
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const TeamSheet = ({ formData, setEvents }) => {
  const navigate = useNavigate();
  const [teamNames, setTeamNames] = useState([]);
  const [fixtures, setFixtures] = useState([]);
  const [isFormValid, setIsFormValid] = useState(false);
  const [isLive, setIsLive] = useState(false);

  useEffect(() => {
    const numTeams = Number(formData.numberOfTeams);
    if (!isNaN(numTeams) && numTeams > 0) {
      setTeamNames(Array(numTeams).fill(''));
    }
  }, [formData.numberOfTeams]);

  useEffect(() => {
    const allFieldsFilled = teamNames.every(name => name.trim() !== '');
    setIsFormValid(allFieldsFilled);
  }, [teamNames]);

  const handleNameChange = (index, event) => {
    const newTeamNames = [...teamNames];
    newTeamNames[index] = event.target.value;
    setTeamNames(newTeamNames);
  };

  const handleRandomFixture = () => {
    const shuffledTeams = [...teamNames].sort(() => 0.5 - Math.random());
    const newFixtures = generateFixtures(shuffledTeams);
    setFixtures(newFixtures);
  };

  const handleCustomFixture = () => {
    const newFixtures = generateFixtures(teamNames);
    setFixtures(newFixtures);
  };

  const generateFixtures = (teams) => {
    const newFixtures = [];
    const numFixtures = Math.floor(teams.length / 2);

    for (let i = 0; i < numFixtures; i++) {
      newFixtures.push({
        team1: teams[i * 2],
        team2: teams[i * 2 + 1] || 'Yet to Decide',
        score1: 0,
        score2: 0,
        time: '',
        date: '',
        location: '',
        matchOver: false,
        winner: ''
      });
    }

    if (teams.length % 2 !== 0) {
      newFixtures.push({
        team1: teams[teams.length - 1],
        team2: 'Yet to Decide',
        score1: 0,
        score2: 0,
        time: '',
        date: '',
        location: '',
        matchOver: false,
        winner: ''
      });
    }

    return newFixtures;
  };

  const handleFixtureChange = (index, field, value) => {
    const newFixtures = [...fixtures];
    newFixtures[index][field] = value;
    setFixtures(newFixtures);
  };

  const handleScoreChange = (index, team, score) => {
    const newFixtures = [...fixtures];
    newFixtures[index][team] = Number(score);
    setFixtures(newFixtures);
  };

  const handleMatchOver = (index) => {
    const newFixtures = [...fixtures];
    newFixtures[index].matchOver = true;
    newFixtures[index].winner = newFixtures[index].score1 > newFixtures[index].score2
      ? newFixtures[index].team1
      : newFixtures[index].score1 < newFixtures[index].score2
        ? newFixtures[index].team2
        : 'Draw';
    setFixtures(newFixtures);
  };

  const handleDone = () => {
    if (!isFormValid) return;
    const newEvent = {
      tournamentName: formData.tournamentName,
      selectedSport: formData.selectedSport,
      fixtures: fixtures
    };
    setEvents((prevEvents) => [...prevEvents, newEvent]);
    navigate('/my-fixture');
  };

  return (
    <div>
      <h1>{formData.tournamentName} - {formData.selectedSport}</h1>
      <h2>{formData.selectedTournament} Tournament</h2>
      <h3>Enter Team Names</h3>
      {teamNames.map((name, index) => (
        <div key={index}>
          <label>Team {index + 1}</label>
          <input
            type="text"
            value={name}
            onChange={(e) => handleNameChange(index, e)}
            placeholder={`Enter team ${index + 1} name`}
          />
        </div>
      ))}
      <button onClick={handleRandomFixture} style={{ backgroundColor: 'green', color: 'white', padding: '10px', margin: '10px' }}>Random Fixture</button>
      <button onClick={handleCustomFixture} style={{ backgroundColor: 'blue', color: 'white', padding: '10px', margin: '10px' }}>Custom Fixture</button>
      <button onClick={() => setIsLive(!isLive)} style={{ backgroundColor: 'orange', color: 'white', padding: '10px', margin: '10px' }}>Live</button>

      {fixtures.length > 0 && (
        <div>
          <h3>Fixtures</h3>
          {fixtures.map((fixture, index) => (
            <div key={index} style={{ border: '1px solid black', padding: '10px', margin: '10px' }}>
              <label>Team 1</label>
              <select
                value={fixture.team1}
                onChange={(e) => handleFixtureChange(index, 'team1', e.target.value)}
              >
                {teamNames.map((name, i) => (
                  <option key={i} value={name}>{name}</option>
                ))}
              </select>
              <span> vs </span>
              <label>Team 2</label>
              <select
                value={fixture.team2}
                onChange={(e) => handleFixtureChange(index, 'team2', e.target.value)}
              >
                {teamNames.map((name, i) => (
                  <option key={i} value={name}>{name}</option>
                ))}
                <option value="Yet to Decide">Yet to Decide</option>
              </select>
              {isLive && (
                <div>
                  <input
                    type="number"
                    value={fixture.score1}
                    onChange={(e) => handleScoreChange(index, 'score1', e.target.value)}
                    placeholder="Score 1"
                    min="0"
                  />
                  <input
                    type="number"
                    value={fixture.score2}
                    onChange={(e) => handleScoreChange(index, 'score2', e.target.value)}
                    placeholder="Score 2"
                    min="0"
                  />
                  <button onClick={() => handleMatchOver(index)} style={{ backgroundColor: 'red', color: 'white', padding: '5px', margin: '5px' }}>Match Over</button>
                </div>
              )}
              <div>
                <label>Time</label>
                <input
                  type="text"
                  value={fixture.time}
                  onChange={(e) => handleFixtureChange(index, 'time', e.target.value)}
                  placeholder="Enter time"
                />
              </div>
              <div>
                <label>Date</label>
                <input
                  type="date"
                  value={fixture.date}
                  onChange={(e) => handleFixtureChange(index, 'date', e.target.value)}
                />
              </div>
              <div>
                <label>Location</label>
                <input
                  type="text"
                  value={fixture.location}
                  onChange={(e) => handleFixtureChange(index, 'location', e.target.value)}
                  placeholder="Enter location"
                />
              </div>
              {fixture.matchOver && (
                <div>
                  <p>Final Score: {fixture.score1} - {fixture.score2}</p>
                  <p>Winner: {fixture.winner}</p>
                </div>
              )}
            </div>
          ))}
          <button
            onClick={handleDone}
            style={{
              backgroundColor: isFormValid ? 'purple' : 'gray',
              color: 'white',
              padding: '10px',
              margin: '10px',
              cursor: isFormValid ? 'pointer' : 'not-allowed'
            }}
          >
            Done
          </button>
        </div>
      )}
    </div>
  );
};

export default TeamSheet;
